<template>
  <div class="header">
    <img class="icon" src="data:image/svg+xml;base64," />
    <div class="title-text">
        <span>{{title}}</span>
    </div>
    <div class="right" v-if="user && user.username">
        <span>{{user.username}}</span>
        <a-button @click="logout" type="link">退出登录</a-button>
    </div>
  </div>
</template>

<script>

export default {
  props: {
      title: {
          type: String,
          default: 'title'
      }
  },
  computed: {
      user() {
          return this.$store.state.authUser
      }
  },
  methods: {
      async logout() {
            const result = await this.$store.dispatch('logout');
            console.log(result);
            if (result.code === 200) {
                console.log(this.$router.push('/login'));
            }
      }
  }
}
</script>

